var gulp = require('kareem/node_modules/gulp');
var mocha = require('kareem/node_modules/gulp-mocha');
var config = require('kareem/package.json');
var jscs = require('kareem/node_modules/gulp-jscs');

gulp.task('mocha', function() {
  return gulp.src('./test/*').
    pipe(mocha({ reporter: 'dot' }));
});

gulp.task('jscs', function() {
  return gulp.src('./index.js').
    pipe(jscs(config.jscsConfig));
});

gulp.task('watch', function() {
  gulp.watch('./index.js', ['jscs', 'mocha']);
});
